document.getElementById("contact-form").addEventListener("submit",(e=>{e.preventDefault();let n=document.createElement("div");n.innerHTML='\n    <div class="message__content">\n    <p>\n      Thank you for send us a message, we will contact you soon!\n    </p>\n    </div>',n.classList.add("message"),document.body.appendChild(n),setTimeout((()=>{document.body.removeChild(n)}),3e3)}));